"use strict";
var vue = require("vue");
const BASE_Z_INDEX = 1e3;
const Z_INDEX_STEP = 1;
class PopupManager {
  constructor() {
    this.zIndex = 0;
    this.popupStack = new Set();
    this.instanceMap = new Map();
  }
  getInstance(id) {
    return this.instanceMap.get(id);
  }
  registerInstance(id, instance) {
    if (!instance)
      return;
    this.instanceMap.set(id, instance);
  }
  deregisterInstance(id) {
    this.instanceMap.delete(id);
  }
  nextZIndex() {
    if (!this.zIndex) {
      this.zIndex = BASE_Z_INDEX;
    } else {
      this.zIndex += Z_INDEX_STEP;
    }
    return this.zIndex;
  }
  open(id) {
    this.popupStack.add(id);
  }
  close(id) {
    this.popupStack.delete(id);
  }
}
let popupManager;
const generateId = (() => {
  let i = 0;
  return (prefix = "") => {
    i += 1;
    return `${prefix}${i}`;
  };
})();
function usePopupManager({
  visible,
  runOnMounted
} = {}) {
  const id = generateId();
  const _zIndex = vue.ref();
  if (!popupManager) {
    popupManager = new PopupManager();
  }
  const zIndex = vue.computed(() => {
    return _zIndex.value || 0;
  });
  const nextZIndex = () => {
    _zIndex.value = popupManager.nextZIndex();
    return _zIndex.value;
  };
  vue.onMounted(() => {
    const instance = vue.getCurrentInstance();
    popupManager.registerInstance(id, instance);
  });
  vue.onBeforeUnmount(() => {
    popupManager.deregisterInstance(id);
  });
  const open = () => {
    popupManager.open(id);
    _zIndex.value = nextZIndex();
  };
  const close = () => {
    popupManager.close(id);
  };
  if (visible) {
    vue.watchEffect(() => {
      if (visible.value) {
        open();
      } else {
        close();
      }
    });
  } else if (runOnMounted) {
    vue.onMounted(() => {
      open();
    });
    vue.onBeforeUnmount(() => {
      close();
    });
  }
  return {
    id,
    zIndex,
    nextZIndex,
    close,
    open
  };
}
module.exports = usePopupManager;
